using Microsoft.AspNetCore.Hosting;
using Microsoft.EntityFrameworkCore;
using Project.Models;
using System;
using System.IO;

namespace Project.Data
{
    public class AnimalContext : DbContext
    {
        [Obsolete]
        private IHostingEnvironment _hostingEnvironment;

        public DbSet<Animal> Animals { get; set; }
        public DbSet<Category> Categories { get; set; }
        public DbSet<Comment> Comments { get; set; }

        [Obsolete]
        public AnimalContext(DbContextOptions<AnimalContext> options, IHostingEnvironment hostingEnvironment) : base(options)
        {
            _hostingEnvironment = hostingEnvironment;
        }

        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {
            modelBuilder.Entity<Category>().HasData(
                new { CategoryId = 1, Name = "Birds", Description = "Birds are a group of warm-blooded vertebrates constituting the class Aves, characterized by feathers, toothless beaked jaws, the laying of hard-shelled eggs, a high metabolic rate, a four-chambered heart, and a strong yet lightweight skeleton. Birds live worldwide and range in size from the 5 cm (2 in) bee hummingbird to the 2.75 m (9 ft) ostrich. There are about ten thousand living species." },
                new { CategoryId = 2, Name = "Reptiles", Description = "Reptiles are tetrapod animals in the class Reptilia /rɛpˈtɪliə/, comprising today's birds, turtles, crocodilians, snakes, amphisbaenians, lizards, tuatara, and their extinct relatives. The study of birds is called ornithology, while the study of the traditional reptile orders, historically combined with that of modern amphibians, is called herpetology." },
                new { CategoryId = 3, Name = "Mammals", Description = "Mammals are a group of vertebrate animals constituting the class Mammalia, and characterized by the presence of mammary glands which in females produce milk for feeding their young, a neocortex (a region of the brain), fur or hair, and three middle ear bones. These characteristics distinguish them from reptiles and birds, from which they diverged in the late Carboniferous, approximately 300 million years ago." },
                new { CategoryId = 4, Name = "Amphibians", Description = "Amphibians are ectothermic, tetrapod vertebrates of the class Amphibia. All living amphibians belong to the group Lissamphibia. They inhabit a wide variety of habitats, with most species living within terrestrial, fossorial, arboreal or freshwater aquatic ecosystems. Thus amphibians typically start out as larvae living in water, but some species have developed behavioural adaptations to bypass this." },
                new { CategoryId = 5, Name = "Fish", Description = "Fish are gill-bearing aquatic craniate animals that lack limbs with digits. They form a sister group to the tunicates, together forming the olfactores. Included in this definition are the living hagfish, lampreys, and cartilaginous and bony fish as well as various extinct related groups." }
            );

            modelBuilder.Entity<Animal>().HasData(
                new { AnimalId = 1, Photo = ImageToByteArray("\\Frogs.jpg"), Name = "Frogs", Age = 1, Description = "An adult frog has a stout body, protruding eyes, anteriorly-attached tongue, limbs folded underneath, and no tail.", CategoryId = 4 },
                new { AnimalId = 2, Photo = ImageToByteArray("\\Newts.jpg"), Name = "Newts", Age = 2, Description = "A newt is a salamander in the subfamily Pleurodelinae. The terrestrial juvenile phase is called an eft. Unlike other members of the family Salamandridae, newts are semiaquatic, alternating between aquatic and terrestrial habitats.", CategoryId = 4 },
                new { AnimalId = 3, Photo = ImageToByteArray("\\Salamanders.jpg"), Name = "Salamanders", Age = 3, Description = "Salamanders are a group of amphibians typically characterized by a lizard-like appearance, with slender bodies, blunt snouts, short limbs projecting at right angles to the body, and the presence of a tail in both larvae and adults. All present-day salamander families are grouped together under the order Urodela. Salamander diversity is highest in the Northern Hemisphere and most species are found in the Holarctic realm, with some species present in the Neotropical realm.", CategoryId = 4 },
                new { AnimalId = 4, Photo = ImageToByteArray("\\Chickens.jpg"), Name = "Chickens", Age = 1, Description = "The chicken (Gallus gallus domesticus) is a type of domesticated fowl, a subspecies of the red junglefowl (Gallus gallus). Chickens are one of the most common and widespread domestic animals, with a total population of 23.7 billion as of 2018. up from more than 19 billion in 2011. There are more chickens in the world than any other bird or domesticated fowl. Humans keep chickens primarily as a source of food (consuming both their meat and eggs) and, less commonly, as pets. Originally raised for cockfighting or for special ceremonies, chickens were not kept for food until the Hellenistic period (4th–2nd centuries BC).", CategoryId = 1 },
                new { AnimalId = 5, Photo = ImageToByteArray("\\Hummingbirds.jpg"), Name = "Hummingbirds", Age = 2, Description = "Hummingbirds are birds native to the Americas and constituting the biological family Trochilidae. They are the smallest of birds, most species measuring 7.5–13 cm (3–5 in) in length. The smallest extant hummingbird species is the 5 cm (2.0 in) bee hummingbird, which weighs less than 2.0 g (0.07 oz). The largest hummingbird species is the 23 cm (9.1 in) giant hummingbird, weighing 18–24g (0.63–0.85 oz). ", CategoryId = 1 },
                new { AnimalId = 6, Photo = ImageToByteArray("\\Falcons.jpg"), Name = "Falcons", Age = 3, Description = "Falcons are birds of prey in the genus Falco, which includes about 40 species. Falcons are widely distributed on all continents of the world except Antarctica, though closely related raptors did occur there in the Eocene.", CategoryId = 1 },
                new { AnimalId = 7, Photo = ImageToByteArray("\\Parrots.jpg"), Name = "Parrots", Age = 4, Description = "Parrots are birds of the roughly 393 species in 92 genera comprising the order Psittaciformes, found mostly in tropical and subtropical regions. The order is subdivided into three superfamilies: the Psittacoidea, the Cacatuoidea (cockatoos), and the Strigopoidea (New Zealand parrots). One-third of all parrot species are threatened by extinction, with higher aggregate extinction risk (IUCN Red List Index) than any other comparable bird group. Parrots have a generally pantropical distribution with several species inhabiting temperate regions in the Southern Hemisphere, as well. The greatest diversity of parrots is in South America and Australasia. ", CategoryId = 1 },
                new { AnimalId = 8, Photo = ImageToByteArray("\\Penguins.jpg"), Name = "Penguins", Age = 5, Description = "Penguins  are a group of aquatic flightless birds. They live almost exclusively in the Southern Hemisphere, with only one species, the Galápagos penguin, found north of the equator. Highly adapted for life in the water, penguins have countershaded dark and white plumage and flippers for swimming. Most penguins feed on krill, fish, squid and other forms of sea life which they catch while swimming underwater. They spend roughly half of their lives on land and the other half in the sea.", CategoryId = 1 },
                new { AnimalId = 9, Photo = ImageToByteArray("\\Eels.jpg"), Name = "Eels", Age = 1, Description = "Eels (often typeset as eels or EELS) is an American rock band, formed in Los Angeles, California in 1995 by singer/songwriter and multi-instrumentalist Mark Oliver Everett, known by the stage name E. Band members have changed across the years, both in the studio and on stage, making Everett the only official member for most of the band's work. Eels' music is often filled with themes about family, death, and lost love. Since 1996, Eels has released thirteen studio albums, seven of which charted in the Billboard 200.", CategoryId = 5 },
                new { AnimalId = 10, Photo = ImageToByteArray("\\Hagfish.jpg"), Name = "Hagfish", Age = 2, Description = "Hagfish, of the class Myxini (also known as Hyperotreti), are eel-shaped, slime-producing marine fish (occasionally called slime eels). They are the only known living animals that have a skull but no vertebral column, although hagfish do have rudimentary vertebrae. Along with lampreys, hagfish are jawless; they are the sister group to jawed vertebrates, and living hagfish remain similar to hagfish from around 300 million years ago.", CategoryId = 5 },
                new { AnimalId = 11, Photo = ImageToByteArray("\\Batoidea.jpg"), Name = "Batoidea", Age = 3, Description = "Batoidea is a superorder of cartilaginous fishes commonly known as rays. They and their close relatives, the sharks, comprise the subclass Elasmobranchii. Rays are the largest group of cartilaginous fishes, with well over 600 species in 26 families. Rays are distinguished by their flattened bodies, enlarged pectoral fins that are fused to the head, and gill slits that are placed on their ventral surfaces.", CategoryId = 5 },
                new { AnimalId = 12, Photo = ImageToByteArray("\\Sharks.jpg"), Name = "Sharks", Age = 4, Description = "Sharks are a group of elasmobranch fish characterized by a cartilaginous skeleton, five to seven gill slits on the sides of the head, and pectoral fins that are not fused to the head. Modern sharks are classified within the clade Selachimorpha (or Selachii) and are the sister group to the rays. However, the term 'shark' has also been used for extinct members of the subclass Elasmobranchii outside the Selachimorpha, such as Cladoselache and Xenacanthus, as well as other Chondrichthyes such as the holocephalid eugenedontidans.", CategoryId = 5 },
                new { AnimalId = 13, Photo = ImageToByteArray("\\Seahorses.jpg"), Name = "Seahorses", Age = 5, Description = "Seahorse (also written sea-horse and sea horse) is the name given to 46 species of small marine fish in the genus Hippocampus. 'Hippocampus' comes from the Ancient Greek hippokampos, itself from hippos meaning 'horse' and kampos meaning 'sea monster'. Having a head and neck suggestive of a horse, seahorses also feature segmented bony armour, an upright posture and a curled prehensile tail. Along with the pipefishes and seadragons they form the order Syngnathidae.", CategoryId = 5 },
                new { AnimalId = 14, Photo = ImageToByteArray("\\Lampreys.jpg"), Name = "Lampreys", Age = 6, Description = "Lampreys (sometimes inaccurately called lamprey eels) are an ancient extant lineage of jawless fish of the order Petromyzontiformes, placed in the superclass Cyclostomata. The adult lamprey may be characterized by a toothed, funnel-like sucking mouth. The common name 'lamprey' is probably derived from Latin lampetra, which may mean 'stone licker' (lambere 'to lick' + petra 'stone'), though the etymology is uncertain. The plural form lamprey is sometimes seen.", CategoryId = 5 },
                new { AnimalId = 15, Photo = ImageToByteArray("\\Bats.jpg"), Name = "Bats", Age = 1, Description = "Bats are mammals of the order Chiroptera. With their forelimbs adapted as wings, they are the only mammals capable of true and sustained flight. Bats are more manoeuvrable than birds, flying with their very long spread-out digits covered with a thin membrane or patagium. The smallest bat, and arguably the smallest extant mammal, is Kitti's hog-nosed bat, which is 29–34 millimetres (1 1⁄8–1 3⁄8 inches) in length, 150 mm (6 in) across the wings and 2–2.6 g (1⁄16–3⁄32 oz) in mass. The largest bats are the flying foxes and the giant golden-crowned flying fox, Acerodon jubatus, which can weigh 1.6 kg (3 1⁄2 lb) and have a wingspan of 1.7 m (5 ft 7 in).", CategoryId = 3 },
                new { AnimalId = 16, Photo = ImageToByteArray("\\Elephants.jpg"), Name = "Elephants", Age = 2, Description = "Elephants are mammals of the family Elephantidae and the largest existing land animals. Three species are currently recognised: the African bush elephant, the African forest elephant, and the Asian elephant. Elephantidae is the only surviving family of the order Proboscidea; extinct members include the mastodons. The family Elephantidae also contains several now-extinct groups, including the mammoths and straight-tusked elephants. African elephants have larger ears and concave backs, whereas Asian elephants have smaller ears, and convex or level backs. Distinctive features of all elephants include a long proboscis called a trunk, tusks, large ear flaps, massive legs, and tough but sensitive skin. The trunk is used for breathing, bringing food and water to the mouth, and grasping objects. Tusks, which are derived from the incisor teeth, serve both as weapons and as tools for moving objects and digging. The large ear flaps assist in maintaining a constant body temperature as well as in communication. The pillar-like legs carry their great weight.", CategoryId = 3 },
                new { AnimalId = 17, Photo = ImageToByteArray("\\Hamsters.jpg"), Name = "Hamsters", Age = 3, Description = "Hamsters are rodents (order Rodentia) belonging to the subfamily Cricetinae, which contains 19 species classified in seven genera. They have become established as popular small house pets. The best-known species of hamster is the golden or Syrian hamster (Mesocricetus auratus), which is the type most commonly kept as pets. Other hamster species commonly kept as pets are the three species of dwarf hamster, Campbell's dwarf hamster (Phodopus campbelli), the winter white dwarf hamster (Phodopus sungorus) and the Roborovski hamster.", CategoryId = 3 },
                new { AnimalId = 18, Photo = ImageToByteArray("\\Rabbits.jpg"), Name = "Rabbits", Age = 4, Description = "Rabbits are small mammals in the family Leporidae of the order Lagomorpha (along with the hare and the pika). Oryctolagus cuniculus includes the European rabbit species and its descendants, the world's 305 breeds of domestic rabbit. Sylvilagus includes 13 wild rabbit species, among them the seven types of cottontail. The European rabbit, which has been introduced on every continent except Antarctica, is familiar throughout the world as a wild prey animal and as a domesticated form of livestock and pet. With its widespread effect on ecologies and cultures, the rabbit (or bunny) is, in many areas of the world, a part of daily life—as food, clothing, a companion, and a source of artistic inspiration.", CategoryId = 3 },
                new { AnimalId = 19, Photo = ImageToByteArray("\\Whales.jpg"), Name = "Whales", Age = 5, Description = "Whales are a widely distributed and diverse group of fully aquatic placental marine mammals. They are an informal grouping within the infraorder Cetacea, usually excluding dolphins and porpoises. Whales, dolphins and porpoises belong to the order Cetartiodactyla, which consists of even-toed ungulates. Their closest living relatives are the hippopotamuses, having diverged about 40 million years ago. The two parvorders of whales, baleen whales (Mysticeti) and toothed whales (Odontoceti), are thought to have split apart around 34 million years ago. Whales consist of eight extant families: Balaenopteridae (the rorquals), Balaenidae (right whales), Cetotheriidae (the pygmy right whale), Eschrichtiidae (the grey whale), Monodontidae (belugas and narwhals), Physeteridae (the sperm whale), Kogiidae (the dwarf and pygmy sperm whale), and Ziphiidae (the beaked whales).", CategoryId = 3 },
                new { AnimalId = 20, Photo = ImageToByteArray("\\Crocodiles.jpg"), Name = "Crocodiles", Age = 1, Description = "Crocodiles (subfamily Crocodylinae) or true crocodiles are large semiaquatic reptiles that live throughout the tropics in Africa, Asia, the Americas and Australia. Crocodylinae, all of whose members are considered true crocodiles, is classified as a biological subfamily. A broader sense of the term crocodile, Crocodylidae (which includes Tomistoma), is not used in this article. The term crocodile here applies to only the species within the subfamily of Crocodylinae. The term is sometimes used even more loosely to include all extant members of the order Crocodilia, which includes the alligators and caimans (family Alligatoridae), the gharial and false gharial (family Gavialidae), and all other living and fossil Crocodylomorpha.", CategoryId = 2 },
                new { AnimalId = 21, Photo = ImageToByteArray("\\Lizards.jpg"), Name = "Lizards", Age = 2, Description = "Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging across all continents except Antarctica, as well as most oceanic island chains. The group is paraphyletic as it excludes the snakes and Amphisbaenia; some lizards are more closely related to these two excluded groups than they are to other lizards. Lizards range in size from chameleons and geckos a few centimeters long to the 3 meter long Komodo dragon.", CategoryId = 2 },
                new { AnimalId = 22, Photo = ImageToByteArray("\\Snakes.jpg"), Name = "Snakes", Age = 3, Description = "Snakes are elongated, legless, carnivorous reptiles of the suborder Serpentes. Like all other squamates, snakes are ectothermic, amniote vertebrates covered in overlapping scales. Many species of snakes have skulls with several more joints than their lizard ancestors, enabling them to swallow prey much larger than their heads with their highly mobile jaws. To accommodate their narrow bodies, snakes' paired organs (such as kidneys) appear one in front of the other instead of side by side, and most have only one functional lung. Some species retain a pelvic girdle with a pair of vestigial claws on either side of the cloaca.", CategoryId = 2 },
                new { AnimalId = 23, Photo = ImageToByteArray("\\Turtles.jpg"), Name = "Turtles", Age = 4, Description = "Turtles are reptiles of the order Testudines. They are characterized by a special bony or cartilaginous shell developed from their ribs that acts as a shield. 'Turtle' may refer to the order as a whole (American English) or to fresh-water and sea-dwelling Testudines (British English). Testudines includes both extant (living) and extinct species. Its earliest known members date from the Middle Jurassic. Turtles are one of the oldest reptile groups, more ancient than snakes or crocodilians. Of the 360 known extant species, some are highly endangered.", CategoryId = 2 },
                new { AnimalId = 24, Photo = ImageToByteArray("\\Tortoises.jpg"), Name = "Tortoises", Age = 5, Description = "Tortoises are reptile species of the family Testudinidae of the order Testudines (from the Latin name for tortoise). They are particularly distinguished from other turtles by being land-dwelling, while many (though not all) other turtle species are at least partly aquatic. Like other turtles, tortoises have a shell to protect from predation and other threats. The shell in tortoises is generally hard, and like other members of the suborder Cryptodira, they retract their necks and heads directly backwards into the shell to protect them.", CategoryId = 2 }
            );

