                new { CommentId = 8, AnimalId = 5, CommentData = "Wow." },
                new { CommentId = 9, AnimalId = 6, CommentData = "Awesome NFL team." },
                new { CommentId = 10, AnimalId = 7, CommentData = "Jacko type can repeat you." },
                new { CommentId = 11, AnimalId = 9, CommentData = "Great electric save." },
                new { CommentId = 12, AnimalId = 12, CommentData = "Require alot food." },
                new { CommentId = 13, AnimalId = 22, CommentData = "Slippery" },
