        public byte[] ImageToByteArray(string path)
        {
            string uploadFolder = Path.Combine(_hostingEnvironment.WebRootPath, "Images");
            string filePath = uploadFolder + path;
            return File.ReadAllBytes(filePath);
        }
    }
}
